import type { NextApiRequest, NextApiResponse } from 'next';
import { analyzeBusinessProfile } from '@/lib/gemini';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const businessProfile = req.body;
    const result = await analyzeBusinessProfile(businessProfile);

    res.status(200).json(result);
  } catch (error) {
    console.error('Analysis error:', error);
    res.status(500).json({ error: 'Failed to analyze business profile' });
  }
}

lib/gemini.ts.content:
import { GoogleGenAI } from "@google/genai";
import { BusinessProfile, ServiceRecommendation, ProjectBlueprint } from "@/types";

const ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

export async function analyzeBusinessProfile(profile: BusinessProfile): Promise<{
  recommendations: ServiceRecommendation[];
  projectBlueprint: ProjectBlueprint;
}> {
  const prompt = `You are an expert IT and cybersecurity consultant analyzing a client's business to provide highly specific, actionable, and valuable service recommendations.

CLIENT BUSINESS PROFILE:
Business Name: ${profile.businessName}
Industry: ${profile.industry}
Business Model: ${profile.businessModel}
Year Established: ${profile.yearEstablished}
Team Size: ${profile.teamSize}
Operating Regions: ${profile.operatingRegions}

CURRENT OPERATIONS:
Core Operations: ${profile.coreOperations}
Workflow Challenges: ${profile.workflowChallenges}
Manual Tasks to Automate: ${profile.manualTasks}
Current Tools/Software: ${profile.currentTools}

TECHNOLOGY STATUS:
Has Website: ${profile.hasWebsite ? 'Yes' : 'No'}
Has Mobile App: ${profile.hasMobileApp ? 'Yes' : 'No'}
Has CRM: ${profile.hasCRM ? 'Yes' : 'No'}
Has ERP: ${profile.hasERP ? 'Yes' : 'No'}
Has Cloud Setup: ${profile.hasCloudSetup ? 'Yes' : 'No'}
Has Admin Tools: ${profile.hasAdminTools ? 'Yes' : 'No'}
Technology Stack: ${profile.technologyStack}
Cybersecurity Practices: ${profile.cybersecurityPractices}
API/Integrations: ${profile.apiIntegrations}

BUSINESS GOALS:
Short-term Goals (6 months): ${profile.shortTermGoals}
Long-term Goals (1-3 years): ${profile.longTermGoals}
Upcoming Launches/Expansions: ${profile.upcomingLaunches}
Desired Automation Areas: ${profile.automationAreas}

CHALLENGES & BLOCKERS:
Revenue Challenges: ${profile.revenueChallenges}
Sales/Marketing Challenges: ${profile.salesMarketingChallenges}
Tech/Infrastructure Bottlenecks: ${profile.techBottlenecks}
Customer Support Challenges: ${profile.customerSupportChallenges}
Compliance/Regulatory Concerns: ${profile.complianceConcerns}

INDUSTRY-SPECIFIC DATA:
Target Customers: ${profile.targetCustomers}
Competitors: ${profile.competitors}
Data Format: ${profile.dataFormat}
Industry-Specific Processes: ${profile.industrySpecificProcesses}

PREFERENCES & CONSTRAINTS:
Budget Preference: ${profile.budgetPreference}
Preferred Solution Type: ${profile.preferredSolutionType}
Deadline/Urgency: ${profile.deadline}
Has Developer Team: ${profile.hasDevTeam ? 'Yes' : 'No'}
Resource Constraints: ${profile.resourceConstraints}

TASK:
Analyze this business deeply and generate 6-12 highly specific, personalized service recommendations across these categories:
1. Process Automation & Optimization
2. Software Solutions
3. Cybersecurity & Risk Reduction
4. Technology Modernization
5. AI & Intelligent Automation
6. Industry-Specific Solutions

For EACH recommendation, provide:
- title: Clear, specific title
- category: One of the 6 categories above
- description: Detailed 2-3 sentence description
- whyNeeded: Why this client specifically needs this (based on their data)
- howItHelps: Concrete benefits and outcomes
- businessImpact: Measurable business impact
- expectedROI: Expected return on investment or value
- priority: High, Medium, or Low based on urgency and impact
- estimatedTimeline: Realistic timeline (e.g., "2-3 months", "4-6 weeks")
- estimatedCost: Budget range (e.g., "Low ($5-15K)", "Medium ($15-50K)", "High ($50K+)")

Also create a high-level PROJECT BLUEPRINT with:
- deliverables: Array of 4-6 key deliverables
- timeline: Overall timeline (e.g., "3-6 months")
- costBracket: Overall cost estimate
- phases: Array of 3-5 project phases with name, duration, and description

Make recommendations unique, specific to their industry, challenges, and goals. Avoid generic advice. Focus on high-value, hard-to-ignore solutions.

Return ONLY valid JSON in this exact format:
{
  "recommendations": [
    {
      "id": "rec-1",
      "title": "...",
      "category": "...",
      "description": "...",
      "whyNeeded": "...",
      "howItHelps": "...",
      "businessImpact": "...",
      "expectedROI": "...",
      "priority": "High|Medium|Low",
      "estimatedTimeline": "...",
      "estimatedCost": "..."
    }
  ],
  "projectBlueprint": {
    "deliverables": ["...", "..."],
    "timeline": "...",
    "costBracket": "...",
    "phases": [
      {
        "name": "...",
        "duration": "...",
        "description": "..."
      }
    ]
  }
}`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.0-flash-exp",
      config: {
        responseMimeType: "application/json",
      },
      contents: prompt,
    });

    const result = JSON.parse(response.text);
    return result;
  } catch (error) {
    console.error("Gemini API error:", error);
    throw new Error("Failed to analyze business profile with Gemini");
  }
}

export async function analyzeUploadedDocument(content: string, fileName: string): Promise<BusinessProfile> {
  const prompt = `You are analyzing a business profile document. Extract all relevant business information and structure it into a complete business profile.

DOCUMENT NAME: ${fileName}

DOCUMENT CONTENT:
${content}

TASK:
Extract and structure the business information into a complete profile. If any field is not found in the document, use "Not specified" or appropriate default values.

Return ONLY valid JSON in this exact format:
{
  "businessName": "...",
  "industry": "...",
  "businessModel": "...",
  "yearEstablished": "...",
  "teamSize": "...",
  "operatingRegions": "...",
  "coreOperations": "...",
  "workflowChallenges": "...",
  "manualTasks": "...",
  "currentTools": "...",
  "hasWebsite": true/false,
  "hasMobileApp": true/false,
  "hasCRM": true/false,
  "hasERP": true/false,
  "hasCloudSetup": true/false,
  "hasAdminTools": true/false,
  "technologyStack": "...",
  "cybersecurityPractices": "...",
  "apiIntegrations": "...",
  "shortTermGoals": "...",
  "longTermGoals": "...",
  "upcomingLaunches": "...",
  "automationAreas": "...",
  "revenueChallenges": "...",
  "salesMarketingChallenges": "...",
  "techBottlenecks": "...",
  "customerSupportChallenges": "...",
  "complianceConcerns": "...",
  "targetCustomers": "...",
  "competitors": "...",
  "dataFormat": "...",
  "industrySpecificProcesses": "...",
  "budgetPreference": "...",
  "preferredSolutionType": "...",
  "deadline": "...",
  "hasDevTeam": true/false,
  "resourceConstraints": "..."
}`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.0-flash-exp",
      config: {
        responseMimeType: "application/json",
      },
      contents: prompt,
    });

    const result = JSON.parse(response.text);
    return result;
  } catch (error) {
    console.error("Gemini document analysis error:", error);
    throw new Error("Failed to analyze uploaded document");
  }
}

pages/api/analyze-document.ts.content:
import type { NextApiRequest, NextApiResponse } from 'next';
import { analyzeUploadedDocument } from '@/lib/gemini';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const { content, fileName } = req.body;

    if (!content || !fileName) {
      return res.status(400).json({ error: 'Missing content or fileName' });
    }

    const profile = await analyzeUploadedDocument(content, fileName);

    res.status(200).json(profile);
  } catch (error) {
    console.error('Document analysis error:', error);
    res.status(500).json({ error: 'Failed to analyze document' });
  }
}

components/Button.tsx.content:
import React from 'react';

interface ButtonProps {
  children: React.ReactNode;
  onClick?: () => void;
  variant?: 'primary' | 'secondary' | 'outline';
  size?: 'sm' | 'md' | 'lg';
  fullWidth?: boolean;
  disabled?: boolean;
  type?: 'button' | 'submit' | 'reset';
  className?: string;
}

export default function Button({
  children,
  onClick,
  variant = 'primary',
  size = 'md',
  fullWidth = false,
  disabled = false,
  type = 'button',
  className = '',
}: ButtonProps) {
  const baseStyles = 'font-semibold rounded-lg transition-all duration-200 flex items-center justify-center gap-2';

  const variantStyles = {
    primary: 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white hover:from-blue-700 hover:to-indigo-700 shadow-lg hover:shadow-xl',
    secondary: 'bg-gradient-to-r from-purple-600 to-pink-600 text-white hover:from-purple-700 hover:to-pink-700 shadow-lg hover:shadow-xl',
    outline: 'border-2 border-blue-600 text-blue-600 hover:bg-blue-50',
  };

  const sizeStyles = {
    sm: 'px-4 py-2 text-sm',
    md: 'px-6 py-3 text-base',
    lg: 'px-8 py-4 text-lg',
  };

  const widthStyle = fullWidth ? 'w-full' : '';
  const disabledStyle = disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer';

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={`${baseStyles} ${variantStyles[variant]} ${sizeStyles[size]} ${widthStyle} ${disabledStyle} ${className}`}
    >
      {children}
    </button>
  );
}

pages/api/fetch-url.ts.content:
import type { NextApiRequest, NextApiResponse } from 'next';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const { url } = req.body;

    if (!url) {
      return res.status(400).json({ error: 'Missing URL' });
    }

    const response = await fetch(url);
    const html = await response.text();

    const textContent = html
      .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
      .replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '')
      .replace(/<[^>]+>/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();

    res.status(200).json({ content: textContent });
  } catch (error) {
    console.error('URL fetch error:', error);
    res.status(500).json({ error: 'Failed to fetch URL content' });
  }
}

pages/dashboard.tsx.content:
import { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import Card from '@/components/Card';
import Button from '@/components/Button';
import { isAuthenticated, getSession, getUser, clearSession, clearUser } from '@/lib/storage';
import { ServiceRecommendation, ProjectBlueprint, ServiceCategory } from '@/types';
import { 
  Sparkles, Target, DollarSign, Clock, TrendingUp, 
  Shield, Cpu, Cloud, Zap, Brain, Building, LogOut 
} from 'lucide-react';
import toast, { Toaster } from 'react-hot-toast';

const categoryIcons: Record<ServiceCategory, any> = {
  'Process Automation & Optimization': Zap,
  'Software Solutions': Cpu,
  'Cybersecurity & Risk Reduction': Shield,
  'Technology Modernization': Cloud,
  'AI & Intelligent Automation': Brain,
  'Industry-Specific Solutions': Building,
};

const categoryColors: Record<ServiceCategory, string> = {
  'Process Automation & Optimization': 'from-yellow-400 to-orange-500',
  'Software Solutions': 'from-blue-400 to-cyan-500',
  'Cybersecurity & Risk Reduction': 'from-red-400 to-pink-500',
  'Technology Modernization': 'from-purple-400 to-indigo-500',
  'AI & Intelligent Automation': 'from-green-400 to-emerald-500',
  'Industry-Specific Solutions': 'from-indigo-400 to-purple-500',
};

export default function Dashboard() {
  const router = useRouter();
  const [recommendations, setRecommendations] = useState<ServiceRecommendation[]>([]);
  const [blueprint, setBlueprint] = useState<ProjectBlueprint | null>(null);
  const [selectedCategory, setSelectedCategory] = useState<ServiceCategory | 'All'>('All');
  const [user, setUser] = useState<any>(null);

  useEffect(() => {
    if (!isAuthenticated()) {
      router.push('/login');
      return;
    }

    const session = getSession();
    const currentUser = getUser();

    if (!session?.recommendations) {
      router.push('/discovery');
      return;
    }

    setUser(currentUser);
    setRecommendations(session.recommendations);
    setBlueprint(session.projectBlueprint || null);
  }, [router]);

  const handleLogout = () => {
    clearSession();
    clearUser();
    toast.success('Logged out successfully');
    router.push('/login');
  };

  const filteredRecommendations = selectedCategory === 'All' 
    ? recommendations 
    : recommendations.filter(r => r.category === selectedCategory);

  const categories = Array.from(new Set(recommendations.map(r => r.category)));

  const priorityColors = {
    High: 'bg-red-100 text-red-700 border-red-300',
    Medium: 'bg-yellow-100 text-yellow-700 border-yellow-300',
    Low: 'bg-green-100 text-green-700 border-green-300',
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Toaster position="top-right" />

      <div className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Sparkles className="w-8 h-8 text-indigo-600" />
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                  Cehpoint
                </h1>
                <p className="text-sm text-gray-600">{user?.companyName}</p>
              </div>
            </div>
            <Button variant="outline" onClick={handleLogout}>
              <LogOut className="w-4 h-4" />
              Logout
            </Button>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-2">
            Your Personalized Service Recommendations
          </h2>
          <p className="text-gray-600">
            AI-powered insights tailored to your business needs
          </p>
        </div>

        <div className="mb-8 flex flex-wrap gap-3">
          <button
            onClick={() => setSelectedCategory('All')}
            className={`px-6 py-3 rounded-lg font-semibold transition-all ${
              selectedCategory === 'All'
                ? 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg'
                : 'bg-white text-gray-700 hover:shadow-md'
            }`}
          >
            All Services ({recommendations.length})
          </button>
          {categories.map((category) => {
            const Icon = categoryIcons[category];
            return (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-6 py-3 rounded-lg font-semibold transition-all flex items-center gap-2 ${
                  selectedCategory === category
                    ? `bg-gradient-to-r ${categoryColors[category]} text-white shadow-lg`
                    : 'bg-white text-gray-700 hover:shadow-md'
                }`}
              >
                <Icon className="w-5 h-5" />
                {category} ({recommendations.filter(r => r.category === category).length})
              </button>
            );
          })}
        </div>

        <div className="grid gap-6 mb-12">
          {filteredRecommendations.map((rec) => {
            const Icon = categoryIcons[rec.category];
            return (
              <Card key={rec.id} hover>
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-start gap-4">
                    <div className={`w-12 h-12 rounded-lg bg-gradient-to-r ${categoryColors[rec.category]} flex items-center justify-center flex-shrink-0`}>
                      <Icon className="w-6 h-6 text-white" />
                    </div>
                    <div className="flex-1">
                      <h3 className="text-xl font-bold text-gray-800 mb-1">
                        {rec.title}
                      </h3>
                      <p className="text-sm text-gray-500">{rec.category}</p>
                    </div>
                  </div>
                  <span className={`px-3 py-1 rounded-full text-xs font-bold border ${priorityColors[rec.priority]}`}>
                    {rec.priority} Priority
                  </span>
                </div>

                <p className="text-gray-700 mb-4">{rec.description}</p>

                <div className="grid md:grid-cols-2 gap-4 mb-4">
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <Target className="w-5 h-5 text-blue-600" />
                      <h4 className="font-semibold text-gray-800">Why You Need This</h4>
                    </div>
                    <p className="text-sm text-gray-700">{rec.whyNeeded}</p>
                  </div>

                  <div className="bg-green-50 p-4 rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <TrendingUp className="w-5 h-5 text-green-600" />
                      <h4 className="font-semibold text-gray-800">How It Helps</h4>
                    </div>
                    <p className="text-sm text-gray-700">{rec.howItHelps}</p>
                  </div>
                </div>

                <div className="grid md:grid-cols-3 gap-4 pt-4 border-t">
                  <div>
                    <div className="flex items-center gap-2 mb-1">
                      <TrendingUp className="w-4 h-4 text-gray-500" />
                      <span className="text-xs font-semibold text-gray-500 uppercase">Business Impact</span>
                    </div>
                    <p className="text-sm font-medium text-gray-800">{rec.businessImpact}</p>
                  </div>

                  <div>
                    <div className="flex items-center gap-2 mb-1">
                      <Clock className="w-4 h-4 text-gray-500" />
                      <span className="text-xs font-semibold text-gray-500 uppercase">Timeline</span>
                    </div>
                    <p className="text-sm font-medium text-gray-800">{rec.estimatedTimeline}</p>
                  </div>

                  <div>
                    <div className="flex items-center gap-2 mb-1">
                      <DollarSign className="w-4 h-4 text-gray-500" />
                      <span className="text-xs font-semibold text-gray-500 uppercase">Investment</span>
                    </div>
                    <p className="text-sm font-medium text-gray-800">{rec.estimatedCost}</p>
                  </div>
                </div>

